import React, { useState ,useEffect} from "react";
import HomePage from "./HomePage.style";
let SHEET_ID = '1AESw7GvFC53W6NUSgiSNw9RhgjyeOn2a5TW7hyTeac8'
let SHEET_TITLE = 'Transcendance';
let SHEET_RANGE = 'D2:D10'

let FULL_URL = ('https://docs.google.com/spreadsheets/d/' + SHEET_ID + '/gviz/tq?sheet=' + SHEET_TITLE + '&range=' + SHEET_RANGE);

const HomeGallery = () => {
    const [rows,setRows] = useState([]);

    useEffect(()=>{
      console.log("hi")
      fetch(FULL_URL)
      .then(res => res.text())
      .then(rep => {
          let data = JSON.parse(rep.substr(47).slice(0,-2));
          setRows(data.table.rows);
          console.log(rows);

      })
    },[]);

return (
    <div>
      {(rows)?rows.map(item=>
      {return(
       <HomePage>
        <img style={{width:'33.3%',padding:'0px'}} src={"https://drive.google.com/uc?export=view&id="+item.c[0].v} alt="drive image"></img>
       </HomePage>)}
      ):null}
    </div>
  );
}

export default HomeGallery;
